import React from "react";
import Meaning from "./Meaning";

export default function Results(props) {
  console.log("ðŸ“© Received results in Results.js:", props.results); // Debugging

  if (!props.results) {
    return null; // ðŸ”´ Hides the message instead of displaying "No results found"
  }

  return (
    <div className="Results">
      <h2>{props.results.word}</h2>
      {props.results.meanings && props.results.meanings.length > 0
        ? props.results.meanings.map((meaning, index) => (
            <div key={index}>
              <Meaning meaning={meaning} />
            </div>
          ))
        : null}{" "}
      {/* ðŸ”´ No message appears when meanings are missing */}
    </div>
  );
}
