import React, { useState } from "react";
import Results from "./Results";
import axios from "axios";

export default function Lexicon() {
  let [keyword, setKeyword] = useState("");
  let [results, setResults] = useState(null);
  let [searched, setSearched] = useState(false); // üî¥ Tracks if a search was made

  function handleResponse(response) {
    console.log("‚úÖ API Response:", response.data);

    if (response.data.word) {
      setResults(response.data);
    } else {
      console.log("üö® No results found in API response!");
      setResults(null);
    }
    setSearched(true); // ‚úÖ Only show "No results found" after a search
  }

  function search(event) {
    event.preventDefault();

    let apiKey = "YOUR_SHECODES_API_KEY"; // Replace with your API key
    let apiUrl = `https://api.shecodes.io/dictionary/v1/define?word=${keyword}&key=${apiKey}`;

    console.log("üîé Fetching from API:", apiUrl);
    axios
      .get(apiUrl)
      .then(handleResponse)
      .catch((error) => {
        console.error("‚ùå API Fetch Error:", error);
        setResults(null);
      });
  }

  function handleKeywordChange(event) {
    setKeyword(event.target.value);
  }

  return (
    <div className="Lexicon">
      <form onSubmit={search}>
        <input
          type="search"
          onChange={handleKeywordChange}
          placeholder="Enter a word..."
        />
      </form>
      {searched && !results && <p>‚ùå No results found</p>}{" "}
      {/* üî¥ Now "No results found" only appears AFTER searching */}
      {results && <Results results={results} />}
    </div>
  );
}
